.invoice-heading {
  margin: 0;
}

.ksa-invoice-table {
  border: 1px solid #888a8e;
  border-collapse: collapse;
  width: 100%;
  margin: 20px 0;
  font-size: 12px;
  font-weight: bold;

}

.ksa-invoice-table.two-columns td:nth-child(2) {
  direction: rtl;
}

.ksa-invoice-table th {
  border: 1px solid #888a8e;
  max-width: 50%;
  padding: 8px;
}

.ksa-invoice-table td {
  padding: 5px;
  border: 1px solid #888a8e;
  max-width: 50%;
}

.ksa-invoice-table thead,
.ksa-invoice-table tfoot {
    font-size: 10px;
    font-weight: bold;
  /*text-transform: uppercase;*/
}

.qr-rtl {
  direction: rtl;
}
.qr-flex{
    display: flex;
    justify-content: space-between;
}

.label {
  text-align: center;
}

  .ksa-vat-format {
    position: relative;
    bottom: 200px;
    left: 150px;
    width: 100px;
    height: 100px;
  }

