}
@page {
            size: A4;
            margin: 0;
        }
@media print {
            body {
                margin: 0;
                padding: 0;
            }
            .page {
                page-break-after: always;
                position: relative;
                height: 100%;
            }
                   .page-content {
                position: absolute;
                top: 0;
                left: 0;
                right: 0;
                bottom: 0;
                padding: 1cm;
                border: 1px solid #ccc;

                font-size: 10pt;
            }
}
